mapper.update:
UPDATE tb_user
set password    = #{password},
    update_time = #{update_time}
where id = #{id};

mapper.select:
select * from tb_user tu

    where tu.username = #{name}

order by tu.create_time

    limit #{offset},#{pageSize}

mapper.update:
update tb_user
set update_time = ${currentTimeMillis}

    ,username = ${username}


    ,`avatar` = ${avatar}


    ,`des` = ${des}


    ,`email` = ${email}

where id = ${id}
